L = thisComp.layer("Shape Layer");
xPad = 50;
yPad = 50;
rL = L.sourceRectAtTime(time, false);
r = sourceRectAtTime(time, false);
hisUL = L.toComp([rL.left, rL.top]);
hisLR = L.toComp([rL.left + rL.width, rL.top + rL.height]);
myUL = toComp([r.left, r.top]);
myLR = toComp([r.left + r.width, r.top + r.height]);
w = (hisLR[0] - hisUL[0]) / (myLR[0] - myUL[0]);
h = (hisLR[1] - hisUL[1]) / (myLR[1] - myUL[1]);
[value[0] * h - xPad / 10, value[1] * h - yPad / 10]

textLayer = thisComp.layer("textLayer");
bbox = textLayer.sourceRectAtTime(time, false);
textLayer.position - textLayer.anchorPoint + [bbox.left * textLayer.scale[0] / 100, bbox.top * textLayer.scale[1] / 100] + [bbox.width * textLayer.scale[0] / 100, bbox.height * textLayer.scale[1] / 100] / 2;
